mapper.select:
SELECT NOTICESEQ, CONTENT, TITLE, REGDATE, READCOUNT FROM(
        select ROW_NUMBER() OVER(ORDER BY NOTICESEQ DESC) AS RNUM, NOTICESEQ, CONTENT, TITLE, REGDATE, READCOUNT from NOTICE 
        )
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}

		SELECT COUNT(*) FROM (
		SELECT NOTICESEQ, CONTENT, TITLE, REGDATE FROM(
                select ROW_NUMBER() OVER(ORDER BY NOTICESEQ DESC) AS RNUM, NOTICESEQ, CONTENT, TITLE, REGDATE from NOTICE 
                )
		)
